import React from "react";

const Index = ({ title, name, lang }) => (
  <>
    <h1 className={lang}>{name}</h1>
    <p className={lang}>{title}</p>
    <style jsx>
      {`
        h1 {
          font-size: 8rem;
        }

        p {
          font-size: 2rem;
        }

        @media screen and (max-width: 600px) {
          h1 {
            font-size: 4rem;
          }
        }
      `}
    </style>
  </>
);

export default Index;
