const CertificateShow = ({ certPath, org }) => (
  <>
    <div>
      <img src={certPath} />
      <h3>{org}</h3>
    </div>
    <style jsx>
      {`
        div {
          margin: 1rem;
        }

        div:hover {
          cursor: pointer
        }

        img {
          width: 360px;
          height: 240px;
          opacity: 1;
        }

        img:hover {
          opacity: 0.5;
        }

        @media screen and (max-width: 600px) {
          img {
            width: 270px;
            height: 185px;
          }
        }
      `}
    </style>
  </>
);

export default CertificateShow;
