import img from "../public/mac.png";
import Layout from "../components/MyLayout.js";
import { Component } from "react";


import img1 from "../public/NYSL_web.png";
import img2 from "../public/TGIF.png";
import img3 from "../public/NYSL_mob.png";
import img4 from "../public/quiz.png";

class Page extends Component {
  constructor(props) {
    super(props);
    this.state = { count: 0 };
  }

  componentDidMount() {
    const ul = document.getElementById("slider");
    const li = ul.childNodes;

    let count = 0;

    // li.forEach((elem) => {
    //   elem.style.display = "none";
    // });
    // li[0].style.display = "flex";
  }
  render() {
    const images = [img1, img2, img3, img4];
    let count = 0;
    const trigger = () => {

      this.setState(state => {

      return state.count < 3 ? { count: state.count + 1 } : { count: 0 };
        console.log(state.count)
        // if(state.count < 3) {
        //   return { count: state.count + 1 }
        // } else {
        //   return { count: 0 };
        // }

        // return {count: state.count + 1}
      })
    };
    const imgStyle = {
      width: "530px",
      height: "330px",
    };
    const listStyle = {
      listStyle: "none",
      display: "flex",
      justifyContent: "center",
      marginTop: "1.5rem",
    };
    const imgList = images.map((image, i) => (
      <li className={"list" + i} key={i} style={listStyle}>
        <img src={image} style={imgStyle} />
      </li>
    ));

    return (
      <div className="main">
        <h1>Title</h1>
        <div className="cover">
          <ul id="slider">{imgList[this.state.count]}</ul>
          <div onClick={trigger}>hey</div>
        </div>
        <style jsx>
          {`
            .main {
              width: 100%;
              height: 92vh;
              background-color: #25252b;
              display: flex;
              flex-direction: column;
              align-items: center;
              justify-content: center;
              color: #fff;
            }
            p {
              font-size: 2rem;
            }
            img {
              width: 530px;
              height: 330px;
            }
            .cover {
              background: url("/mac.png");
              width: 700px;
              height: 400px;
              background-repeat: no-repeat;
              background-size: cover;
            }
            li {
              display: flex;
              justify-content: center;
              list-style: none;
              margin-top: 1.5rem;
            }
          `}
        </style>
      </div>
    );
  }
}


