import React from "react";

const Index = ({ title, name, lang }) => (
  <>
    <h1 className={lang}>{name}</h1>
    <p className={lang}>{title}</p>
    <style jsx>
      {`
        h1 {
          font-size: 8rem;
        }

        p {
          font-size: 2rem;
        }
      `}
    </style>
  </>
);

export default Index;
